import React, { useState, useEffect } from "react";
import Modal from "react-modal";

const CorporateEvents = () => {
  const [selectedImage, setSelectedImage] = useState(null);
  const [fade, setFade] = useState(false);

  useEffect(() => {
    try {
      const rootEl = document.getElementById("root") || document.body;
      if (rootEl && typeof Modal.setAppElement === "function") {
        Modal.setAppElement(rootEl);
      }
    } catch (err) {
      console.warn("Modal setup skipped:", err);
    }
  }, []);

  const images = [
    "https://images.unsplash.com/photo-1521737604893-d14cc237f11d",
    "https://images.unsplash.com/photo-1503424886309-4a3d94e15148",
    "https://images.unsplash.com/photo-1504384308090-c894fdcc538d",
    "https://images.unsplash.com/photo-1551836022-4c4c79ecde51",
    "https://images.unsplash.com/photo-1485217988980-11786ced9454",
    "https://images.unsplash.com/photo-1501281668745-f7f57925c3b4",
  ];

  const openModal = (img) => {
    setSelectedImage(img);
    setFade(true);
  };

  const closeModal = () => {
    setFade(false);
    setTimeout(() => setSelectedImage(null), 300);
  };

  return (
    <div className="min-h-screen bg-gradient-to-r from-purple-100 via-blue-100 to-pink-100 flex flex-col items-center justify-center p-8 transition-all duration-300">
      <h1 className="text-4xl font-bold text-gray-800 mb-6 text-center">
        Corporate Events
      </h1>
      <p className="text-lg text-gray-700 mb-10 text-center max-w-2xl">
        Celebrate success, teamwork, and innovation with our corporate events.
        Explore our moments of joy and achievement below.
      </p>

      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6 w-full max-w-5xl">
        {images.map((img, i) => (
          <img
            key={i}
            src={img}
            alt={`Corporate Event ${i + 1}`}
            className="rounded-2xl shadow-md cursor-pointer transform transition-transform duration-300 hover:scale-105"
            onClick={() => openModal(img)}
          />
        ))}
      </div>

      <Modal
        isOpen={!!selectedImage}
        onRequestClose={closeModal}
        className={`fixed inset-0 flex items-center justify-center bg-black/70 transition-opacity duration-300 ${
          fade ? "opacity-100" : "opacity-0"
        }`}
        overlayClassName="fixed inset-0 bg-black/50 flex items-center justify-center"
      >
        <div
          className={`relative p-4 rounded-2xl bg-white shadow-lg transform transition-all duration-300 ${
            fade ? "scale-100 opacity-100" : "scale-95 opacity-0"
          }`}
        >
          <img
            src={selectedImage}
            alt="Event"
            className="max-h-[80vh] max-w-[80vw] object-contain rounded-lg"
          />
          <button
            onClick={closeModal}
            className="absolute top-3 right-3 px-4 py-2 rounded-lg bg-gradient-to-r from-blue-500 to-purple-600 hover:from-blue-700 hover:to-purple-700 text-white font-semibold transition-all duration-300"
          >
            âœ• Close
          </button>
        </div>
      </Modal>
    </div>
  );
};

export default CorporateEvents;
